import { MAIN_PATH, SEARCH_PATH } from 'constant';
import { ChangeEvent, KeyboardEvent, useEffect, useRef, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './style.css';

//              components(header)      //
export default function Header() {
  // navigator //
  const navigate = useNavigate();

  // event handler 로고 클릭 이벤트 처리함수 //
  const onLogoClickHandler = () => {
    navigate(MAIN_PATH());
  };

  // components(Search button)                  //
  const SearchButton = () => {
    // state: searchButton button state           //
    const searchButtonRef = useRef<HTMLDivElement | null>(null);
    // state: search button state           //
    const [status, setStatus] = useState<Boolean>(false);
    // state: searchword state           //
    const [Word, setWord] = useState<string>('');
    // state: searchword path variable status           //
    const { searchWord } = useParams();

    // event handler(searchWord change)     //
    const onSearchWordChangeHandler = (
      event: ChangeEvent<HTMLInputElement>,
    ) => {
      const value = event.target.value;
      setWord(value);
    };
    // event handler(searchWord key)     //
    const onSearchWordKeyDownHandler = (
      event: KeyboardEvent<HTMLInputElement>,
    ) => {
      if (event.key !== 'Enter') return;
      if (!searchButtonRef.current) return;
      searchButtonRef.current.click();
    };

    // event handler(search icon click)     //
    const onSearchButtonClickHandler = () => {
      if (!status) {
        setStatus(!status);
        return;
      }
      navigate(SEARCH_PATH(Word));
    };
    // effect 검색어 path variable 변경 될 때 마다 실행할 함수     //
    useEffect(() => {
      if (searchWord) {
        setWord(searchWord);
        setStatus(true);
      }
    }, [searchWord]);

    if (!status)
      // render click false //
      return (
        <div className="icon-button" onClick={onSearchButtonClickHandler}>
          <div className="search-light-icon"></div>
        </div>
      );
    // render click true //
    return (
      <div className="header-search-input-box">
        <input
          className="header-search-input-box"
          type="text"
          placeholder="검색어를 입력해주세요."
          value={Word}
          onChange={onSearchWordChangeHandler}
          onKeyDown={onSearchWordKeyDownHandler}
        />
        <div
          ref={searchButtonRef}
          className="icon-button"
          onClick={onSearchButtonClickHandler}
        >
          <div className="icon search-light-icon"></div>
        </div>
      </div>
    );
  };
  // render   //
  return (
    <div id="header">
      <div className="header-container">
        <div className="header-left-box" onClick={onLogoClickHandler}>
          <div className="icon-box">
            <div className="icon logo-dark-icon"></div>
          </div>
          <div className="header-logo">{'FOR JP'}</div>
        </div>
        <div className="header-right-box">
          <SearchButton />
        </div>
      </div>
    </div>
  );
}
