import * as Image from 'assets/point.png';
import {
  ChangeEvent,
  Dispatch,
  KeyboardEvent,
  SetStateAction,
  forwardRef,
} from 'react';
import './JoinStyles.css';

//          interface: Propperties(JOINFORM)                    //
interface Props {
  label: string;
  type: 'text' | 'password';
  placeholder: string;
  value: string;
  setValue: Dispatch<SetStateAction<string>>;
  error: boolean;

  icon?: string;
  onButtonClick?: () => void;

  message?: string;

  onKeyDown?: (event: KeyboardEvent<HTMLInputElement>) => void;
}

//          page: JOINFORM 컴포넌트 <render: JOINFORM>          //
const JoinForm = forwardRef<HTMLInputElement, Props>((props: Props, ref) => {
  //                  state: Propperties                                 //
  const { label, type, placeholder, value, error, icon, message } = props;
  const { setValue, onButtonClick, onKeyDown } = props;

  //            event handler: input 값 변경 이벤트 처리 함수             //
  const onChangeHandler = (event: ChangeEvent<HTMLInputElement>) => {
    const { value } = event.target;
    setValue(value);
  };
  //            event handler: input 키 이벤트 처리 함수             //
  const onKeyDownhandler = (event: KeyboardEvent<HTMLInputElement>) => {
    if (!onKeyDown) return;
    onKeyDown(event);
  };
  return (
    <div className="Join">
      <img src={Image} id="image" alt="BigCo Inc. logo" />
      <div className="overlay"></div>
      <div className="content">
        <div className="inputbox-label">{label}</div>
        <div
          className={error ? 'inputbox-container-error' : 'inputbox-container'}
        >
          <input
            ref={ref}
            type={type}
            className="input"
            placeholder={placeholder}
            value={value}
            onChange={onChangeHandler}
            onKeyDown={onKeyDown}
          />
          {onButtonClick !== undefined && (
            <div className="icon-button">
              {icon !== undefined && <div className={'icon ${icon}'}></div>}
            </div>
          )}
        </div>
        {message !== undefined && (
          <div className="inputbox-message">{message}</div>
        )}
      </div>
    </div>
  );
});

export default JoinForm;
