import { useState } from 'react';
import { Nav } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './BarStyles.css';

function Bar() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleClick = () => {
    setIsMenuOpen(false); // 버튼을 누르면 메뉴가 닫히도록 설정
    window.scrollTo({ top : 0, behavior: 'smooth' }); // 버튼을 누르면 페이지 맨 위로 스크롤되도록 설정
  };

  return (
    <div className={`navbar ${isMenuOpen ? 'navbar-bg' : ''}`}>
      <div className="logo">
        <h2>FOR P.</h2>
      </div>
      <button className="toggle-button" onClick={toggleMenu}>
        <div className={isMenuOpen ? 'bar bar1' : 'bar'}></div>
        <div className={isMenuOpen ? 'bar bar2' : 'bar'}></div>
        <div className={isMenuOpen ? 'bar bar3' : 'bar'}></div>
      </button>
      <Nav className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
        <Link to="/" className="nav-link" onClick={handleClick}>
          HOME
        </Link>
        <Link to="/joinForm" className="nav-link" onClick={handleClick}>
          JOIN
        </Link>
        <Link to="/shareForm" className="nav-link" onClick={handleClick}>
          SHARE
        </Link>
        <Link to="/complainForm" className="nav-link" onClick={handleClick}>
          COMPLAIN
        </Link>
      </Nav>
      {isMenuOpen && <div className="overlay"></div>}{' '}
      {/* 메뉴가 열렸을 때만 overlay를 표시합니다. */}
    </div>
  );
}

export default Bar;
